package com.wp.cloud.shop.config;

import com.fasterxml.jackson.core.JsonProcessingException;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.wp.cloud.shop.common.enumerate.WSMsgType;
import com.wp.cloud.shop.common.model.websocket.WSClientMessage;
import lombok.extern.slf4j.Slf4j;
import org.java_websocket.client.WebSocketClient;
import org.java_websocket.enums.ReadyState;
import org.java_websocket.handshake.ServerHandshake;
import org.springframework.context.annotation.Configuration;
import org.springframework.scheduling.annotation.EnableScheduling;
import org.springframework.scheduling.annotation.Scheduled;

import java.net.URI;
import java.net.URISyntaxException;
import java.util.Date;
import java.util.HashMap;
import java.util.Map;

/**
 * @Title: shop-cloud--com.wp.cloud.shop.config.ShopWebSocketClient
 * @Description:
 * @Author suanmilk
 * @CreateTime: 2019-05-03 18:44
 */
@Slf4j
@Configuration
@EnableScheduling
public class ShopWebSocketClient {

    private ObjectMapper mapper = new ObjectMapper();

    private WebSocketClient client;

    private Map<String, String> headers = new HashMap<>();

    private String wsuri = "ws://localhost:8011/shop-ws/webSocketServer";

    private String data = "";

    public ShopWebSocketClient() {
        try {
            // 初始化链接地址
            URI uri = new URI(wsuri);
            // 初始化headers
            // TODO 根据配置文件参数生成必要的header认证参数
            headers.put("token", "facemeng");
            headers.put("connect-id", "123");
            headers.put("connect-type", "CUSTOMER");

            client = new WebSocketClient(uri, headers) {

                @Override
                public void onOpen(ServerHandshake serverHandshake) {
                    log.debug("连接成功");
                    // TODO 连接成功后是否需要处理未发送成功的信息
                }

                @Override
                public void onMessage(String s) {
                    log.debug("接收到服务端信息：{}", s);

                }

                @Override
                public void onClose(int i, String s, boolean b) {
                    log.debug("连接已关闭，等待重新连接...");
                }

                @Override
                public void onError(Exception e) {
                    e.printStackTrace();
                    log.debug("客户端发生错误,即将关闭!");
                }
            };
        } catch (URISyntaxException e) {
            e.printStackTrace();
        }
    }

    /**
     * 每隔1分钟，客户端验证websocket客户端连接状态，若没有创建连接则创建，若连接关闭了则重新连接
     *
     * @throws Exception
     */
    @Scheduled(fixedRate = 30000)
    public void connect() throws Exception {
        // 创建连接
        ReadyState readyState = client.getReadyState();
        log.debug("客户端连接状态：{}", readyState);
        switch (readyState) {
            case NOT_YET_CONNECTED:
                log.debug("创建websocket连接...");
                client.connect();
                break;
            case CLOSED:
                log.debug("websocket重新连接...");
                client.reconnect();
                break;
        }
    }

    /**
     * 每隔30秒，客户端向服务端发送心跳信息
     */
    @Scheduled(fixedRate = 10000)
    public void ping() {
        WSClientMessage clientMessage = new WSClientMessage();
        clientMessage.setId("123");
        clientMessage.setMsgType(WSMsgType.PING);
        StringBuffer buffer = new StringBuffer(data);
        buffer.append(data).append(data);
        clientMessage.setData(buffer.toString());
        log.debug("心跳请求：{}", buffer.length());
//        clientMessage.setData(new Date());
        sendMessage(clientMessage);
    }

    /**
     * 客户端向服务端的发送消息
     *
     * @param clientMessage
     */
    public void sendMessage(WSClientMessage clientMessage) {
        if (client.isOpen()) {
            try {
                String message = mapper.writeValueAsString(clientMessage);
                client.send(message);
            } catch (JsonProcessingException e) {
                e.printStackTrace();
            }
        } else {
            log.debug("连接未打开，不能向服务端发送信息");
            // TODO 未成功发送的消息是否需要保存，供延时处理
        }
    }
}
